import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "I love Titove",
      bool: true
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result)
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0])
  }

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true
    });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <p className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </p>
          <div>
            <textarea
              type="text"
              className="input"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

          <div class="input__wrapper">
            <input
              type="file"
              name="file"
              class="input input__file" multiple
              className="inputFile"
              accept=".txt,.doc,.docx"
              onChange={(e) => this.InputFile(e)}
            />
            <label for="input__file" class="input__file-button">
      <span class="input__file-icon-wrapper"><img class="input__file-icon"  alt="Выбрать файл" width="25"/></span>
      <span class="input__file-button-text">Выберите файл</span>
   </label>

          </div>
          </div>
            <button className="button" onClick={this.Analyze}>
             Анализировать
            </button>
            <button className="button" onClick={this.Delete}>
             Стереть
           </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <p className="output">{this.state.text}</p>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;
