import React, { Component } from "react";
import "./App.css";
import PostData from './test.json';
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "да сколько можно", // !!! ИЗМЕНИТЬ
      AnalyzeText: [],
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    //if (e!==undefined){
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result)
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0])
  //}  else alert ('bla')
  }

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      axios.post('http://127.0.0.1:8000/analyze/text/', {"text": this.state.text}/*text_to_json*/)
        .then(res => console.log(res))
        .catch(error => console.log("SOMETHING WENT WRONG ",error));

        axios.get('http://127.0.0.1:8000/analyze/words/')
        .then(res => {
          alert()
            this.setState({
                AnalyzeText: res.data
            })
        })
        this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true
    });
  };

  Subject = () => {
    if (this.state.sub === true)
    this.setState({
      sub: false
    });
    else this.setState({
      sub: true
    });
  };

  Object = () => {
    if (this.state.obj === true)
    this.setState({
      obj: false
    });
    else this.setState({
      obj: true
    });
  };

  Predicate = () => {
    if (this.state.pre === true)
    this.setState({
      pre: false
    });
    else this.setState({
      pre: true
    });
  };

  Attribute = () => {
    if (this.state.att === true)
    this.setState({
      att: false
    });
    else this.setState({
      att: true
    });
  };

  SelectAll = () => {
    if (this.state.att === true)
    this.setState({
      sub: false,
      obj: false,
      pre: false,
      att: false
    });
    else this.setState({
      sub: true,
      obj: true,
      pre: true,
      att: true
    });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <p className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </p>
          <div>
            <textarea
              type="text"
              className="inputText"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

          <div>
            <input
              type="file"
              className="inputFile"
              accept=".txt,.doc,.docx"
              onChange={(e) => this.InputFile(e)}
            />

          </div>
          </div>
            <button className="button" onClick={this.Analyze}>
             Анализировать
            </button>
            <button className="button" onClick={this.Delete}>
             Стереть
           </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <div className="output" >
          {this.state.AnalyzeText.map((analyzeText, index)=>{

             return <p className="output_words dropdown"> {analyzeText.word}<div className=" dropdown-content ">
                <a href="#">Начальная форма: {analyzeText.normal_form}</a>
                <a href="#">Часть речи: {analyzeText.speech_part}</a>
                <a href="#">Падеж: {analyzeText.case}</a>
                <a href="#">Род: {analyzeText.gender}</a>
                <a href="#">Число: {analyzeText.number}</a>
                <a href="#">Лицо: {analyzeText.person}</a>
                <a href="#">Залог: {analyzeText.voice}</a>
                <a href="#">Член предложения: {analyzeText.role}</a>
              </div>
              </p>

          })}

          </div>
          <div>

          <input type="checkbox"  onClick={this.Subject}/>Подлежащее

           <input type="checkbox" onClick={this.Predicate}/>
             Сказуемое

           <input type="checkbox" onClick={this.Object}/>
             Дополнение

           <input type="checkbox" onClick={this.Attribute}/>
             Определение
             <input type="checkbox" /*onClick={this.SelectAll}*//>
             Выбрать всё
           </div>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;

