import React, { Component } from "react";
import "./App.css";
import PostData from './test.json'
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "I love Titove",
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result)
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0])
  }

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      let text = {text: this.state.text}
      let json = JSON.stringify(text);
      //alert(json);
      axios.post('http://127.0.0.1:8000/analyze/text', {
            json
        })
        .then(res => console.log(res))
        .catch(error => (console.log(",kzzzzzzzzzzzzzzzzz "+error)));
      this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true
    });
  };

  Subject = () => {
    if (this.state.sub === true)
    this.setState({
      sub: false
    });
    else this.setState({
      sub: true
    });
  };

  Object = () => {
    if (this.state.obj === true)
    this.setState({
      obj: false
    });
    else this.setState({
      obj: true
    });
  };

  Predicate = () => {
    if (this.state.pre === true)
    this.setState({
      pre: false
    });
    else this.setState({
      pre: true
    });
  };

  Attribute = () => {
    if (this.state.att === true)
    this.setState({
      att: false
    });
    else this.setState({
      att: true
    });
  };

  SelectAll = () => {
    if (this.state.att === true)
    this.setState({
      sub: false,
      obj: false,
      pre: false,
      att: false
    });
    else this.setState({
      sub: true,
      obj: true,
      pre: true,
      att: true
    });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <p className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </p>
          <div>
            <textarea
              type="text"
              className="input"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

          <div>
            <input
              type="file"
              className="inputFile"
              accept=".txt,.doc,.docx"
              onChange={(e) => this.InputFile(e)}
            />

          </div>
          </div>
            <button className="button" onClick={this.Analyze}>
             Анализировать
            </button>
            <button className="button" onClick={this.Delete}>
             Стереть
           </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <p className="output" >
          {PostData.map((postDetail, index)=>{
            if (postDetail.speech_part == 'objact' && this.state.obj === true) 
            return <div className="output_words Object dropdown"> {postDetail.word}<div className=" dropdown-content">
              <a href="#">{postDetail.person}</a>
              <a href="#">{postDetail.number}</a>
              <a href="#">{postDetail.voice}</a>

                </div>
              </div>
            if (postDetail.speech_part == 'subjact' && this.state.sub === true) 
              return <p className="output_words Subject">{postDetail.word}</p>
            if (postDetail.speech_part == 'predicate' && this.state.pre === true) 
             return <p className="output_words Predicate">{postDetail.word}</p>
            if (postDetail.speech_part == 'attribute' && this.state.att === true) 
             return <p className="output_words Attribute">{postDetail.word}</p>
            else return <div className="output_words dropdown"> {postDetail.word}<div className=" dropdown-content ">
                <a href="#">Начальная форма: {postDetail.normal_form}</a>
                <a href="#">Часть речи: {postDetail.speech_part}</a>
                <a href="#">Падеж: {postDetail.case}</a>
                <a href="#">Род: {postDetail.gender}</a>
                <a href="#">Число: {postDetail.number}</a>
                <a href="#">Лицо: {postDetail.person}</a>
                <a href="#">Залог: {postDetail.voice}</a>
                <a href="#">Член предложения: {postDetail.role}</a>
              </div>
              </div>
          })}

          </p>
          <div>

          <input type="checkbox"  onClick={this.Subject}/>Подлежащее

           <input type="checkbox" onClick={this.Predicate}/>
             Сказуемое

           <input type="checkbox" onClick={this.Object}/>
             Дополнение

           <input type="checkbox" onClick={this.Attribute}/>
             Определение
             <input type="checkbox" /*onClick={this.SelectAll}*//>
             Выбрать всё
           </div>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;

