import React, { Component } from "react";
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "I love Titove",
      bool: true
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile (e)  {
    //  var file = input.files[0];
    /* this.setState({
      text: event.target.value
    });*/
    let file=e.target.file;
    // alert(file);
    let reader = new FileReader();
    reader.readAsText(file[0]);
    reader.onload=(e)=>{
      alert(e.target.result);
    }
    /* var file = files[0];*/
    //var reader = new FileReader();
    /*  reader.onload = function (e) {
      // Когда это событие активируется, данные готовы.
      // Вставляем их в страницу в элемент <div>
      var output = document.getElementById("fileOutput");   
      output.textContent = e.target.result;
  };*/
    /*reader.onload = function(file) {
      var contents = event.target.result;
      console.log("Содержимое файла: " + contents);
  };

  reader.onerror = function(event) {
      console.error("Файл не может быть прочитан! код " + event.target.error.code);
  };*/
    //reader.readAsText(file);
  };

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true
    });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <p className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </p>
          <div>
            <textarea
              type="text"
              className="input"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

<div>
            <input
            id="file"
              type="file"
              className="inputFile"
              accept=".txt,.doc,.docx"
              //onChange={this.InputFile(this)}
              //onInput={this.InputFile}
              onChange={(e)=>this.InputFile(e)}
            />
            <button  onClick={this.InputChange}>submit</button>
            </div>
          </div>

          <button className="button" onClick={this.Analyze}>
            Анализировать
          </button>
          <button className="button" onClick={this.Delete}>
            Стереть
          </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <p className="output">{this.state.text}</p>
          <script>
            var link = document.querySelector('link[rel=import]'); var content =
            link.import.querySelector('#intro-dm');
            document.body.appendChild(content.cloneNode(true));
          </script>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;
