import React, { Component } from "react";
import "./App.css";
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "Я ехал с охоты вечером. Гроза надвигалась. Впереди огромная лиловая туча медленно поднималась из-за леса. Надо мною и мне навстречу неслись длинные серые облака. Дорога вилась передо мною между густыми кустами орешника. Я подвигался вперед с трудом. Сильный ветер внезапно загудел в вышине, деревья забушевали, крупные капли дождя резко застучали, зашлепали по листьям. Сверкнула молния, и гроза разразилась. Дождь полил ручьями. Я поехал шагом и скоро вынужден был остановиться и приютиться к широкому кусту. Я терпеливо ожидал конца ненастья. Вдруг на дороге почудилась мне высокая фигура. Неизвестный словно вырос из-под земли.", // !!! ИЗМЕНИТЬ
      analyzeText: [],
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false,
      ad: false,
      loading: false
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    //if (e!==undefined){
    e.preventDefault();
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0]);
  };

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //let loading = true;
      console.log(this.state.loading);
      this.setState({
        loading: true 
      })
      console.log(this.state.loading);
      axios.post("http://127.0.0.1:8000/analyze/text/",{ text: this.state.text })
        .then((res) => console.log(res))
        .catch((error) => console.log("SOMETHING WENT WRONG ", error))

      .then(() => {
        axios.get("http://127.0.0.1:8000/analyze/words/")

      .then((res) => {
        this.setState({
          analyzeText: res.data
        });
      })
      .then(() => this.setState({
        bool: false
      }));

    });

     /* while (this.state.loading === true){
        setTimeout(300);
      }*/

     /* sleep(2000)
  .then(() => { console.log("World!"); })*/
      console.log(this.state.loading);


    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    //axios.post("http://127.0.0.1:8000/analyze/text/",{ text: "" })
    this.setState({
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false,
      ad: false
    });
  };

  Subject = () => {
    if (this.state.sub === true)
      this.setState({
        sub: false
      });
    else
      this.setState({
        sub: true
      });
  };

  Object = () => {
    if (this.state.obj === true)
      this.setState({
        obj: false
      });
    else
      this.setState({
        obj: true
      });
  };

  Predicate = () => {
    if (this.state.pre === true)
      this.setState({
        pre: false
      });
    else
      this.setState({
        pre: true
      });
  };

  Adverbial_modifier = () => {
    if (this.state.pre === true)
      this.setState({
        ad: false
      });
    else
      this.setState({
        ad: true
      });
  };

  Attribute = () => {
    if (this.state.att === true)
      this.setState({
        att: false
      });
    else
      this.setState({
        att: true
      });
  };

  /*ChangeVal = () => {
    if (this.state.att === true)
      this.setState({
        att: false
      });
    else
      this.setState({
        att: true
      });
  };*/

  SelectAll = () => {
    if (this.state.att === true)
      this.setState({
        sub: false,
        obj: false,
        pre: false,
        att: false
      });
    else
      this.setState({
        sub: true,
        obj: true,
        pre: true,
        att: true
      });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <div className="Zag">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </div>
          <div>
            <textarea
              type="text"
              className="inputText"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

            <div>
              <input
                type="file"
                className="inputFile"
                accept=".txt,.doc,.docx"
                onChange={(e) => this.InputFile(e)}
              />
            </div>
          </div>
          <button className="button" onClick={this.Analyze}>
            Анализировать
          </button>
          <button className="button" onClick={this.Delete}>
            Стереть
          </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <div className="output">
            {this.state.analyzeText.map((postDetail, index) => {
              if (postDetail.role === "дополнение" && this.state.obj === true)
                return (
                  <div className="output_words Object dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <p>Начальная форма: {postDetail.normal_form}</p>
                      <p>Часть речи: {postDetail.speech_part}</p>
                      <p>Падеж: {postDetail.case}</p>
                      <p>Род: {postDetail.gender}</p>
                      <p>Число: {postDetail.number}</p>
                      <p>Лицо: {postDetail.person}</p>
                      <p>Залог: {postDetail.voice}</p>
                      <p>Член предложения: {postDetail.role}</p>
                    </div>
                  </div>
                );
              if (postDetail.role === "подлежащее" && this.state.sub === true)
                return (
                  <div className="output_words Subject dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <p>Начальная форма: {postDetail.normal_form}</p>
                      <p>Часть речи: {postDetail.speech_part}</p>
                      <p>Падеж: {postDetail.case}</p>
                      <p>Род: {postDetail.gender}</p>
                      <p>Число: {postDetail.number}</p>
                      <p>Лицо: {postDetail.person}</p>
                      <p>Залог: {postDetail.voice}</p>
                      <p>Член предложения: {postDetail.role}</p>
                    </div>
                  </div>
                );
              if (postDetail.role === "сказуемое" && this.state.pre === true)
                return (
                  <div className="output_words Predicate dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <p>Начальная форма: {postDetail.normal_form}</p>
                      <p>Часть речи: {postDetail.speech_part}</p>
                      <p>Падеж: {postDetail.case}</p>
                      <p>Род: {postDetail.gender}</p>
                      <p>Число: {postDetail.number}</p>
                      <p>Лицо: {postDetail.person}</p>
                      <p>Залог: {postDetail.voice}</p>
                      <p>Член предложения: {postDetail.role}</p>
                    </div>
                  </div>
                );
              if (postDetail.role === "определение" && this.state.att === true)
                return (
                  <div className="output_words Attribute dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <p>Начальная форма: {postDetail.normal_form}</p>
                      <p>Часть речи: {postDetail.speech_part}</p>
                      <p>Падеж: {postDetail.case}</p>
                      <p>Род: {postDetail.gender}</p>
                      <p>Число: {postDetail.number}</p>
                      <p>Лицо: {postDetail.person}</p>
                      <p>Залог: {postDetail.voice}</p>
                      <p>Член предложения: {postDetail.role}</p>
                    </div>
                  </div>
                );
              if (postDetail.role === "обстоятельство" && this.state.ad === true)
                return (
                  <div
                    className="output_words Adverbial_modifier dropdown"
                    key={index}
                  >
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <p>Начальная форма: {postDetail.normal_form}</p>
                      <p>Часть речи: {postDetail.speech_part}</p>
                      <p>Падеж: {postDetail.case}</p>
                      <p>Род: {postDetail.gender}</p>
                      <p>Число: {postDetail.number}</p>
                      <p>Лицо: {postDetail.person}</p>
                      <p>Залог: {postDetail.voice}</p>
                      <p>Член предложения: {postDetail.role}</p>
                    </div>
                  </div>
                );
              else
                return (
                  <div className="output_words dropdown" key={index}>
                    {" "}
                    {postDetail.word}
                    <div className=" dropdown-content ">
                      <p>Начальная форма: {postDetail.normal_form}</p>
                      <p>Часть речи: {postDetail.speech_part}</p>
                      <p>Падеж: {postDetail.case}</p>
                      <p>Род: {postDetail.gender}</p>
                      <p>Число: {postDetail.number}</p>
                      <p>Лицо: {postDetail.person}</p>
                      <p>Залог: {postDetail.voice}</p>
                      <p>Член предложения: {postDetail.role}</p>
                    </div>
                  </div>
                );
            })}
          </div>
          <div>
            <input type="checkbox" className="button notInLine" onClick={this.Subject} />
            Подлежащее
            <input type="checkbox" className="button" onClick={this.Predicate} />
            Сказуемое
            <input type="checkbox" className="button" onClick={this.Object} />
            Дополнение
            <input type="checkbox" className="button" onClick={this.Attribute} />
            Определение
            <input type="checkbox" className="button" onClick={this.Adverbial_modifier} />
            Обстоятельство
            <input type="checkbox" className="button" /*onClick={this.SelectAll}*/ />
            Выбрать всё
          </div>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;
