import React, { Component } from "react";
import "./App.css";
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "Я ехал с охоты вечером. Гроза надвигалась. Впереди огромная лиловая туча медленно поднималась из-за леса. Надо мною и мне навстречу неслись длинные серые облака. Дорога вилась передо мною между густыми кустами орешника. Я подвигался вперед с трудом. Сильный ветер внезапно загудел в вышине, деревья забушевали, крупные капли дождя резко застучали, зашлепали по листьям. Сверкнула молния, и гроза разразилась. Дождь полил ручьями. Я поехал шагом и скоро вынужден был остановиться и приютиться к широкому кусту. Я терпеливо ожидал конца ненастья. Вдруг на дороге почудилась мне высокая фигура. Неизвестный словно вырос из-под земли.", // !!! ИЗМЕНИТЬ
      analyzeText: [],
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false,
      ad: false,
      loading: false
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    //if (e!==undefined){
    e.preventDefault();
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0]);
  };

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //let loading = true;

      this.setState({
        loading: true 
      })

      axios.post("http://127.0.0.1:8000/analyze/text/",{ text: this.state.text })
        .then((res) => console.log(res))
        .catch((error) => console.log("SOMETHING WENT WRONG ", error))

      .then(() => {
        console.log(this.state.loading);
        axios.get("http://127.0.0.1:8000/analyze/words/")

      .then((res) => {
        this.setState({
          analyzeText: res.data
        });
        console.log(this.state.loading);
      })
      .then(() => {this.setState({
        bool: false,
        loading: false
      })
      console.log(this.state.loading);
    });

    });

     /* while (this.state.loading === true){
        setTimeout(300);
      }*/

     /* sleep(2000)
  .then(() => { console.log("World!"); })*/

    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    //axios.post("http://127.0.0.1:8000/analyze/text/",{ text: "" })
    this.setState({
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false,
      ad: false
    });
  };

  Subject = () => {
    if (this.state.sub === true)
      this.setState({
        sub: false
      });
    else
      this.setState({
        sub: true
      });
  };

  Object = () => {
    if (this.state.obj === true)
      this.setState({
        obj: false
      });
    else
      this.setState({
        obj: true
      });
  };

  Predicate = () => {
    if (this.state.pre === true)
      this.setState({
        pre: false
      });
    else
      this.setState({
        pre: true
      });
  };

  Adverbial_modifier = () => {
    if (this.state.pre === true)
      this.setState({
        ad: false
      });
    else
      this.setState({
        ad: true
      });
  };

  Attribute = () => {
    if (this.state.att === true)
      this.setState({
        att: false
      });
    else
      this.setState({
        att: true
      });
  };

  /*ChangeVal = () => {
    if (this.state.att === true)
      this.setState({
        att: false
      });
    else
      this.setState({
        att: true
      });
  };*/

  SelectAll = () => {
    if (this.state.att === true)
      this.setState({
        sub: false,
        obj: false,
        pre: false,
        att: false
      });
    else
      this.setState({
        sub: true,
        obj: true,
        pre: true,
        att: true
      });
  };

  render() { return (
    <div>
      <div id="circularG">
      <div id="circularG_1" className="circularG"></div>
      <div id="circularG_2" className="circularG"></div>
      <div id="circularG_3" className="circularG"></div>
      <div id="circularG_4" className="circularG"></div>
      <div id="circularG_5" className="circularG"></div>
      <div id="circularG_6" className="circularG"></div>
      <div id="circularG_7" className="circularG"></div>
      <div id="circularG_8" className="circularG"></div>
      <div className="Zag2">Идет анализ...</div>
      </div>
    </div>
  );}
}

export default App;
