import React, { Component } from "react";
import "./App.css";
import PostData from './test.json'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "I love Titove",
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result)
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0])
  }

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      let text = {text: this.state.text}
      let json = JSON.stringify(text);
      //alert(json);
      this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true
    });
  };

  Subject = () => {
    if (this.state.sub === true)
    this.setState({
      sub: false
    });
    else this.setState({
      sub: true
    });
  };

  Object = () => {
    if (this.state.obj === true)
    this.setState({
      obj: false
    });
    else this.setState({
      obj: true
    });
  };

  Predicate = () => {
    if (this.state.pre === true)
    this.setState({
      pre: false
    });
    else this.setState({
      pre: true
    });
  };

  Attribute = () => {
    if (this.state.att === true)
    this.setState({
      att: false
    });
    else this.setState({
      att: true
    });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <p className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </p>
          <div>
            <textarea
              type="text"
              className="input"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

          <div>
            <input
              type="file"
              className="inputFile"
              accept=".txt,.doc,.docx"
              onChange={(e) => this.InputFile(e)}
            />

          </div>
          </div>
            <button className="button" onClick={this.Analyze}>
             Анализировать
            </button>
            <button className="button" onClick={this.Delete}>
             Стереть
           </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <p className="output" >
          {PostData.map((postDetail, index)=>{
            if (postDetail.speech_part == 'objact' && this.state.obj === true) 
            return <p className="output_words Object dropdown-content">{postDetail.word}

            </p>
            if (postDetail.speech_part == 'subjact' && this.state.sub === true) 
              return <p className="output_words Subject">{postDetail.word}</p>
            if (postDetail.speech_part == 'predicate' && this.state.pre === true) 
             return <p className="output_words Predicate">{postDetail.word}</p>
            if (postDetail.speech_part == 'attribute' && this.state.att === true) 
             return <p className="output_words Attribute">{postDetail.word}</p>
            else return <p className="output_words">{postDetail.word}</p>
          })}

          </p>
          <div>

          <input type="checkbox"  onClick={this.Subject}/>Подлежащее

           <input type="checkbox" onClick={this.Predicate}/>
             Сказуемое

           <input type="checkbox" onClick={this.Object}/>
             Дополнение

           <input type="checkbox" onClick={this.Attribute}/>
             Определение

           </div>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;

