import React, { Component } from "react";
import "./App.css";
import axios from 'axios';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "Я люблю Титова", // !!! ИЗМЕНИТЬ
      analyzeText: [],
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false,
      ad: false
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    //if (e!==undefined){
    e.preventDefault();
    const reader = new FileReader();
    reader.onload = async (e) => {
      const text = e.target.result;
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0]);
    //}  else alert ('bla')
  };

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = async () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      axios
        .post(
          "http://127.0.0.1:8000/analyze/text/",
          { text: this.state.text } /*text_to_json*/
        )
        .then((res) => console.log(res))
        .catch((error) => console.log("SOMETHING WENT WRONG ", error));

      axios.get("http://127.0.0.1:8000/analyze/words/").then((res) => {
        this.setState({
          analyzeText: res.data
        });
      });
      this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true,
      obj: false,
      sub: false,
      pre: false,
      att: false,
      ad: false
    });
  };

  Subject = () => {
    if (this.state.sub === true)
      this.setState({
        sub: false
      });
    else
      this.setState({
        sub: true
      });
  };

  Object = () => {
    if (this.state.obj === true)
      this.setState({
        obj: false
      });
    else
      this.setState({
        obj: true
      });
  };

  Predicate = () => {
    if (this.state.pre === true)
      this.setState({
        pre: false
      });
    else
      this.setState({
        pre: true
      });
  };

  Adverbial_modifier = () => {
    if (this.state.pre === true)
      this.setState({
        ad: false
      });
    else
      this.setState({
        ad: true
      });
  };

  Attribute = () => {
    if (this.state.att === true)
      this.setState({
        att: false
      });
    else
      this.setState({
        att: true
      });
  };

  ChangeVal = (e) => {
    if (this.state.e === true)
      this.setState({
        e: false
      });
    else
      this.setState({
        e: true
      });
  };

  SelectAll = () => {
    if (this.state.att === true)
      this.setState({
        sub: false,
        obj: false,
        pre: false,
        att: false
      });
    else
      this.setState({
        sub: true,
        obj: true,
        pre: true,
        att: true
      });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <div className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </div>
          <div>
            <textarea
              type="text"
              className="inputText"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

            <div>
              <input
                type="file"
                className="inputFile"
                accept=".txt,.doc,.docx"
                onChange={(e) => this.InputFile(e)}
              />
            </div>
          </div>
          <button className="button" onClick={this.Analyze}>
            Анализировать
          </button>
          <button className="button" onClick={this.Delete}>
            Стереть
          </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <div className="output">
            {this.state.analyzeText.map((postDetail, index) => {
              if (postDetail.role === "дополнение" && this.state.obj === true)
                return (
                  <div className="output_words Object dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <a>Начальная форма: {postDetail.normal_form}</a>
                      <a>Часть речи: {postDetail.speech_part}</a>
                      <a>Падеж: {postDetail.case}</a>
                      <a>Род: {postDetail.gender}</a>
                      <a>Число: {postDetail.number}</a>
                      <a>Лицо: {postDetail.person}</a>
                      <a>Залог: {postDetail.voice}</a>
                      <a>Член предложения: {postDetail.role}</a>
                    </div>
                  </div>
                );
              if (postDetail.role == "подлежащее" && this.state.sub === true)
                return (
                  <div className="output_words Subject dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <a>Начальная форма: {postDetail.normal_form}</a>
                      <a>Часть речи: {postDetail.speech_part}</a>
                      <a>Падеж: {postDetail.case}</a>
                      <a>Род: {postDetail.gender}</a>
                      <a>Число: {postDetail.number}</a>
                      <a>Лицо: {postDetail.person}</a>
                      <a>Залог: {postDetail.voice}</a>
                      <a>Член предложения: {postDetail.role}</a>
                    </div>
                  </div>
                );
              if (postDetail.role == "сказуемое" && this.state.pre === true)
                return (
                  <div className="output_words Predicate dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <a>Начальная форма: {postDetail.normal_form}</a>
                      <a>Часть речи: {postDetail.speech_part}</a>
                      <a>Падеж: {postDetail.case}</a>
                      <a>Род: {postDetail.gender}</a>
                      <a>Число: {postDetail.number}</a>
                      <a>Лицо: {postDetail.person}</a>
                      <a>Залог: {postDetail.voice}</a>
                      <a>Член предложения: {postDetail.role}</a>
                    </div>
                  </div>
                );
              if (postDetail.role == "определение" && this.state.att === true)
                return (
                  <div className="output_words Attribute dropdown" key={index}>
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <a>Начальная форма: {postDetail.normal_form}</a>
                      <a>Часть речи: {postDetail.speech_part}</a>
                      <a>Падеж: {postDetail.case}</a>
                      <a>Род: {postDetail.gender}</a>
                      <a>Число: {postDetail.number}</a>
                      <a>Лицо: {postDetail.person}</a>
                      <a>Залог: {postDetail.voice}</a>
                      <a>Член предложения: {postDetail.role}</a>
                    </div>
                  </div>
                );
              if (
                postDetail.role == "обстоятельство" &&
                this.state.ad === true
              )
                return (
                  <div
                    className="output_words Adverbial_modifier dropdown"
                    key={index}
                  >
                    {postDetail.word}
                    <div className=" dropdown-content">
                      <a>Начальная форма: {postDetail.normal_form}</a>
                      <a>Часть речи: {postDetail.speech_part}</a>
                      <a>Падеж: {postDetail.case}</a>
                      <a>Род: {postDetail.gender}</a>
                      <a>Число: {postDetail.number}</a>
                      <a>Лицо: {postDetail.person}</a>
                      <a>Залог: {postDetail.voice}</a>
                      <a>Член предложения: {postDetail.role}</a>
                    </div>
                  </div>
                );
              else
                return (
                  <div className="output_words dropdown" key={index}>
                    {" "}
                    {postDetail.word}
                    <div className=" dropdown-content ">
                      <a>Начальная форма: {postDetail.normal_form}</a>
                      <a>Часть речи: {postDetail.speech_part}</a>
                      <a>Падеж: {postDetail.case}</a>
                      <a>Род: {postDetail.gender}</a>
                      <a>Число: {postDetail.number}</a>
                      <a>Лицо: {postDetail.person}</a>
                      <a>Залог: {postDetail.voice}</a>
                      <a>Член предложения: {postDetail.role}</a>
                    </div>
                  </div>
                );
            })}
          </div>
          <div>
            <input type="checkbox" onClick={this.Subject("sub")} />
            Подлежащее
            <input type="checkbox" onClick={this.Predicate} />
            Сказуемое
            <input type="checkbox" onClick={this.Object} />
            Дополнение
            <input type="checkbox" onClick={this.Attribute} />
            Определение
            <input type="checkbox" onClick={this.Adverbial_modifier} />
            Обстоятельство
            <input type="checkbox" /*onClick={this.SelectAll}*/ />
            Выбрать всё
          </div>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;
