import React, { Component } from "react";
import "./App.css";
import PostData from './test.json'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: "I love Titove",
      bool: true
    };
  }

  InputChange = (event) => {
    this.setState({
      text: event.target.value
    });
  };

  InputFile = async (e) => {
    e.preventDefault()
    const reader = new FileReader()
    reader.onload = async (e) => { 
      const text = (e.target.result)
      this.setState({
        text: text
      });
    };
    reader.readAsText(e.target.files[0])
  }

  Delete = () => {
    this.setState({
      text: ""
    });
  };

  EnterKeyPress = (event) => {
    if (event.key === "Enter") {
      this.Analyze();
    }
  };

  Analyze = () => {
    if (this.state.text !== "") {
      //alert([this.state.text])
      let text = {text: this.state.text}
      let json = JSON.stringify(text);
      alert(json);
      this.setState({
        bool: false
      });
    } else alert("Введите текст или выберите файл");
  };

  Back = () => {
    this.setState({
      bool: true
    });
  };

  render() {
    if (this.state.bool === true) {
      return (
        <div className="App">
          <p className="p">
            Введите текст или загрузите текстовый файл (txt,doc,docx)
          </p>
          <div>
            <textarea
              type="text"
              className="input"
              placeholder="Введите текст для анализа"
              value={this.state.text}
              onChange={this.InputChange}
              onKeyPress={this.EnterKeyPress}
            />

          <div>
            <input
              type="file"
              className="inputFile"
              accept=".txt,.doc,.docx"
              onChange={(e) => this.InputFile(e)}
            />

          </div>
          </div>
            <button className="button" onClick={this.Analyze}>
             Анализировать
            </button>
            <button className="button" onClick={this.Delete}>
             Стереть
           </button>
        </div>
      );
    } else {
      return (
        <div className="App">
          <p className="output" >{this.state.text}
          {PostData.map((postDetail, index)=>{
            return <p className="output">{postDetail.word}</p>
          })}</p>
          <button className="button_out" onClick={this.Subject}>
             Подлежащее
           </button>
           <button className="button_out" onClick={this.Predicate}>
             Сказуемое
           </button>
           <button className="button_out" onClick={this.Object}>
             Дополнение
           </button>
           <button className="button_out" onClick={this.Otribute}>
             Определение
           </button>
          <div>
            <button className="button" onClick={this.Back}>
              Назад
            </button>
          </div>
        </div>
      );
    }
  }
}

export default App;
